import React, { useState, useEffect, useCallback, useRef } from 'react';
import './App.css';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './components/ui/card';
import { Button } from './components/ui/button';
import { Input } from './components/ui/input';
import { Progress } from './components/ui/progress';
import { Badge } from './components/ui/badge';
import { Separator } from './components/ui/separator';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './components/ui/tabs';
import { Alert, AlertDescription } from './components/ui/alert';
import { Textarea } from './components/ui/textarea';
import { Label } from './components/ui/label';
import { 
  Upload, 
  Video, 
  FileText, 
  Clock, 
  CheckCircle, 
  AlertCircle, 
  ExternalLink,
  Trash2,
  RefreshCw,
  Download,
  Globe,
  Zap
} from 'lucide-react';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const App = () => {
  const [activeTab, setActiveTab] = useState('upload');
  const [jobs, setJobs] = useState([]);
  const [dragActive, setDragActive] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('info');

  // Form state
  const [videoFile, setVideoFile] = useState(null);
  const [subtitleFile, setSubtitleFile] = useState(null);
  const [videoUrl, setVideoUrl] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const videoInputRef = useRef(null);
  const subtitleInputRef = useRef(null);
  const dropZoneRef = useRef(null);

  // Status polling
  useEffect(() => {
    const interval = setInterval(fetchJobs, 3000);
    fetchJobs();
    return () => clearInterval(interval);
  }, []);

  const fetchJobs = async () => {
    try {
      const response = await fetch(`${API}/jobs`);
      if (response.ok) {
        const jobsData = await response.json();
        setJobs(jobsData);
      }
    } catch (error) {
      console.error('Failed to fetch jobs:', error);
    }
  };

  const showMessage = (msg, type = 'info') => {
    setMessage(msg);
    setMessageType(type);
    setTimeout(() => setMessage(''), 5000);
  };

  // Drag and drop handlers
  const handleDrag = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const files = Array.from(e.dataTransfer.files);

    files.forEach(file => {
      if (file.type.startsWith('video/')) {
        setVideoFile(file);
      } else if (file.name.endsWith('.srt') || file.name.endsWith('.ass')) {
        setSubtitleFile(file);
      }
    });

    if (files.length > 0) {
      showMessage(`Added ${files.length} file(s)`, 'success');
    }
  }, []);

  // File upload handlers
  const handleVideoFileChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      if (file.size > 3 * 1024 * 1024 * 1024) {
        showMessage('Video file must be under 3GB', 'error');
        return;
      }
      setVideoFile(file);
      showMessage('Video file selected', 'success');
    }
  };

  const handleSubtitleFileChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      setSubtitleFile(file);
      showMessage('Subtitle file selected', 'success');
    }
  };

  // Upload functions
  const uploadFiles = async () => {
    if (!videoFile || !subtitleFile) {
      showMessage('Please select both video and subtitle files', 'error');
      return;
    }

    setIsUploading(true);
    setUploadProgress(0);

    try {
      const formData = new FormData();
      formData.append('video_file', videoFile);
      formData.append('subtitle_file', subtitleFile);
      if (title) formData.append('title', title);
      if (description) formData.append('description', description);

      const response = await fetch(`${API}/upload-files`, {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        showMessage('Upload successful! Processing started.', 'success');

        // Reset form
        setVideoFile(null);
        setSubtitleFile(null);
        setTitle('');
        setDescription('');
        if (videoInputRef.current) videoInputRef.current.value = '';
        if (subtitleInputRef.current) subtitleInputRef.current.value = '';

        // Switch to queue tab
        setActiveTab('queue');
        fetchJobs();
      } else {
        const error = await response.json();
        showMessage(`Upload failed: ${error.detail}`, 'error');
      }
    } catch (error) {
      showMessage(`Upload error: ${error.message}`, 'error');
    } finally {
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  const uploadFromUrl = async () => {
    if (!videoUrl || !subtitleFile) {
      showMessage('Please provide video URL and subtitle file', 'error');
      return;
    }

    setIsUploading(true);

    try {
      const formData = new FormData();
      formData.append('video_url', videoUrl);
      formData.append('subtitle_file', subtitleFile);
      if (title) formData.append('title', title);
      if (description) formData.append('description', description);

      const response = await fetch(`${API}/upload-url`, {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        showMessage('Upload successful! Processing started.', 'success');

        // Reset form
        setVideoUrl('');
        setSubtitleFile(null);
        setTitle('');
        setDescription('');
        if (subtitleInputRef.current) subtitleInputRef.current.value = '';

        // Switch to queue tab
        setActiveTab('queue');
        fetchJobs();
      } else {
        const error = await response.json();
        showMessage(`Upload failed: ${error.detail}`, 'error');
      }
    } catch (error) {
      showMessage(`Upload error: ${error.message}`, 'error');
    } finally {
      setIsUploading(false);
    }
  };

  const deleteJob = async (jobId) => {
    try {
      const response = await fetch(`${API}/job/${jobId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        showMessage('Job deleted successfully', 'success');
        fetchJobs();
      }
    } catch (error) {
      showMessage('Failed to delete job', 'error');
    }
  };

  const formatFileSize = (bytes) => {
    if (!bytes) return 'Unknown';
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return Math.round(bytes / Math.pow(1024, i) * 100) / 100 + ' ' + sizes[i];
  };

  const formatTime = (seconds) => {
    if (!seconds) return '0s';
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m ${secs}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'failed':
        return <AlertCircle className="h-4 w-4 text-red-500" />;
      case 'processing':
        return <RefreshCw className="h-4 w-4 text-blue-500 animate-spin" />;
      default:
        return <Clock className="h-4 w-4 text-yellow-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'bg-green-500';
      case 'failed': return 'bg-red-500';
      case 'processing': return 'bg-blue-500';
      default: return 'bg-yellow-500';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="flex items-center justify-center mb-4">
            <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-3 rounded-full mr-4">
              <Zap className="h-8 w-8 text-white" />
            </div>
            <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              Video Subtitle Encoder
            </h1>
          </div>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Fast video subtitle hard-encoding tool with Archive.org upload. 
            Process large files up to 3GB with professional quality.
          </p>
        </div>

        {/* Message Alert */}
        {message && (
          <Alert className={`mb-6 ${messageType === 'error' ? 'border-red-200 bg-red-50' : 
                                   messageType === 'success' ? 'border-green-200 bg-green-50' : 
                                   'border-blue-200 bg-blue-50'}`}>
            <AlertDescription className={messageType === 'error' ? 'text-red-800' : 
                                        messageType === 'success' ? 'text-green-800' : 'text-blue-800'}>
              {message}
            </AlertDescription>
          </Alert>
        )}

        {/* Main Content */}
        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-2 mb-8">
            <TabsTrigger value="upload" className="flex items-center space-x-2">
              <Upload className="h-4 w-4" />
              <span>Upload & Process</span>
            </TabsTrigger>
            <TabsTrigger value="queue" className="flex items-center space-x-2">
              <Clock className="h-4 w-4" />
              <span>Processing Queue ({jobs.length})</span>
            </TabsTrigger>
          </TabsList>

          {/* Upload Tab */}
          <TabsContent value="upload">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">

              {/* File Upload Section */}
              <Card className="border-2 border-dashed border-gray-200 hover:border-blue-300 transition-colors">
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Video className="h-5 w-5 text-blue-600" />
                    <span>Upload Files</span>
                  </CardTitle>
                  <CardDescription>
                    Select video file (up to 3GB) and subtitle file (.srt or .ass)
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  {/* Drag & Drop Zone */}
                  <div
                    ref={dropZoneRef}
                    onDragEnter={handleDrag}
                    onDragLeave={handleDrag}
                    onDragOver={handleDrag}
                    onDrop={handleDrop}
                    className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
                      dragActive 
                        ? 'border-blue-500 bg-blue-50' 
                        : 'border-gray-300 bg-gray-50 hover:bg-gray-100'
                    }`}
                  >
                    <Upload className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-lg font-medium text-gray-700 mb-2">
                      Drop files here or click to browse
                    </p>
                    <p className="text-sm text-gray-500">
                      Supports MP4, AVI, MOV, MKV and SRT, ASS subtitle files
                    </p>
                  </div>

                  <div className="space-y-4 mt-6">
                    {/* Video File Input */}
                    <div>
                      <Label htmlFor="video-file" className="flex items-center space-x-2 mb-2">
                        <Video className="h-4 w-4" />
                        <span>Video File</span>
                      </Label>
                      <Input
                        id="video-file"
                        ref={videoInputRef}
                        type="file"
                        accept="video/*"
                        onChange={handleVideoFileChange}
                        className="cursor-pointer"
                      />
                      {videoFile && (
                        <p className="text-sm text-green-600 mt-1">
                          ✓ {videoFile.name} ({formatFileSize(videoFile.size)})
                        </p>
                      )}
                    </div>

                    {/* Subtitle File Input */}
                    <div>
                      <Label htmlFor="subtitle-file" className="flex items-center space-x-2 mb-2">
                        <FileText className="h-4 w-4" />
                        <span>Subtitle File</span>
                      </Label>
                      <Input
                        id="subtitle-file"
                        ref={subtitleInputRef}
                        type="file"
                        accept=".srt,.ass"
                        onChange={handleSubtitleFileChange}
                        className="cursor-pointer"
                      />
                      {subtitleFile && (
                        <p className="text-sm text-green-600 mt-1">
                          ✓ {subtitleFile.name} ({formatFileSize(subtitleFile.size)})
                        </p>
                      )}
                    </div>

                    <Button 
                      onClick={uploadFiles} 
                      disabled={isUploading || !videoFile || !subtitleFile}
                      className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
                    >
                      {isUploading ? (
                        <>
                          <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                          Uploading...
                        </>
                      ) : (
                        <>
                          <Upload className="h-4 w-4 mr-2" />
                          Start Processing
                        </>
                      )}
                    </Button>
                  </div>
                </CardContent>
              </Card>

              {/* URL Upload Section */}
              <Card className="border-2 border-gray-200">
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Globe className="h-5 w-5 text-green-600" />
                    <span>Upload from URL</span>
                  </CardTitle>
                  <CardDescription>
                    Download video from URL and process with subtitles
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="video-url">Video URL</Label>
                      <Input
                        id="video-url"
                        type="url"
                        placeholder="https://example.com/video.mp4"
                        value={videoUrl}
                        onChange={(e) => setVideoUrl(e.target.value)}
                      />
                    </div>

                    <div>
                      <Label htmlFor="subtitle-file-url">Subtitle File</Label>
                      <Input
                        id="subtitle-file-url"
                        type="file"
                        accept=".srt,.ass"
                        onChange={handleSubtitleFileChange}
                        className="cursor-pointer"
                      />
                      {subtitleFile && (
                        <p className="text-sm text-green-600 mt-1">
                          ✓ {subtitleFile.name}
                        </p>
                      )}
                    </div>

                    <Button 
                      onClick={uploadFromUrl} 
                      disabled={isUploading || !videoUrl || !subtitleFile}
                      className="w-full bg-gradient-to-r from-green-600 to-blue-600 hover:from-green-700 hover:to-blue-700"
                    >
                      {isUploading ? (
                        <>
                          <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                          Processing...
                        </>
                      ) : (
                        <>
                          <Download className="h-4 w-4 mr-2" />
                          Download & Process
                        </>
                      )}
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Metadata Section */}
            <Card className="mt-8">
              <CardHeader>
                <CardTitle>Optional Metadata</CardTitle>
                <CardDescription>
                  Add title and description for your processed video
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="title">Title</Label>
                    <Input
                      id="title"
                      placeholder="Enter video title"
                      value={title}
                      onChange={(e) => setTitle(e.target.value)}
                    />
                  </div>
                  <div className="md:col-span-2">
                    <Label htmlFor="description">Description</Label>
                    <Textarea
                      id="description"
                      placeholder="Enter video description"
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                      rows={3}
                    />
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Queue Tab */}
          <TabsContent value="queue">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between">
                <div>
                  <CardTitle>Processing Queue</CardTitle>
                  <CardDescription>
                    Monitor your video processing jobs and encoding progress
                  </CardDescription>
                </div>
                <Button 
                  onClick={fetchJobs} 
                  variant="outline" 
                  size="sm"
                  className="ml-auto"
                >
                  <RefreshCw className="h-4 w-4 mr-2" />
                  Refresh
                </Button>
              </CardHeader>
              <CardContent>
                {jobs.length === 0 ? (
                  <div className="text-center py-12">
                    <Clock className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">No jobs yet</h3>
                    <p className="text-gray-500">Upload some videos to see them here</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {jobs.map((job) => (
                      <Card key={job.id} className="border-l-4 border-l-blue-500">
                        <CardContent className="p-6">
                          <div className="flex items-start justify-between mb-4">
                            <div className="flex items-center space-x-3">
                              {getStatusIcon(job.status)}
                              <div>
                                <h4 className="font-medium text-gray-900">
                                  {job.video_filename}
                                </h4>
                                <p className="text-sm text-gray-500">
                                  Subtitles: {job.subtitle_filename}
                                </p>
                              </div>
                            </div>
                            <div className="flex items-center space-x-2">
                              <Badge 
                                variant="secondary" 
                                className={`${getStatusColor(job.status)} text-white`}
                              >
                                {job.status.toUpperCase()}
                              </Badge>
                              {job.status === 'failed' || job.status === 'completed' ? (
                                <Button
                                  onClick={() => deleteJob(job.id)}
                                  variant="ghost"
                                  size="sm"
                                  className="text-red-500 hover:text-red-700"
                                >
                                  <Trash2 className="h-4 w-4" />
                                </Button>
                              ) : null}
                            </div>
                          </div>

                          {/* Progress Bar */}
                          {job.status === 'processing' && (
                            <div className="mb-4">
                              <div className="flex justify-between text-sm text-gray-600 mb-2">
                                <span>Processing...</span>
                                <span>{Math.round(job.progress)}%</span>
                              </div>
                              <Progress value={job.progress} className="h-2" />
                            </div>
                          )}

                          {/* Job Details */}
                          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                            <div>
                              <p className="text-gray-500">File Size</p>
                              <p className="font-medium">{formatFileSize(job.video_size)}</p>
                            </div>
                            <div>
                              <p className="text-gray-500">Encoding Time</p>
                              <p className="font-medium">{formatTime(job.encoding_time)}</p>
                            </div>
                            <div>
                              <p className="text-gray-500">Created</p>
                              <p className="font-medium">
                                {new Date(job.created_at).toLocaleString()}
                              </p>
                            </div>
                            <div>
                              <p className="text-gray-500">Status</p>
                              <p className="font-medium capitalize">{job.status}</p>
                            </div>
                          </div>

                          {/* Archive URL */}
                          {job.archive_url && (
                            <div className="mt-4 pt-4 border-t">
                              <div className="flex items-center justify-between">
                                <div>
                                  <p className="text-sm text-gray-500">Archive.org URL</p>
                                  <p className="text-sm font-mono text-blue-600 truncate">
                                    {job.archive_url}
                                  </p>
                                </div>
                                <Button
                                  onClick={() => window.open(job.archive_url, '_blank')}
                                  variant="outline"
                                  size="sm"
                                >
                                  <ExternalLink className="h-4 w-4 mr-2" />
                                  View
                                </Button>
                              </div>
                            </div>
                          )}

                          {/* Error Message */}
                          {job.error_message && (
                            <div className="mt-4 p-3 bg-red-50 border border-red-200 rounded-md">
                              <p className="text-sm text-red-800">
                                <strong>Error:</strong> {job.error_message}
                              </p>
                            </div>
                          )}
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>

        {/* Footer */}
        <div className="text-center mt-12 py-8 border-t border-gray-200">
          <p className="text-gray-500">
            Fast video subtitle hard-encoding with Archive.org integration
          </p>
          <p className="text-sm text-gray-400 mt-2">
            Supports files up to 3GB • FFmpeg powered • Professional quality
          </p>
        </div>
      </div>
    </div>
  );
};

export default App;
